$(document).ready(function(){
	if($(window).width() < $(window).height()){
		if($(window).height() > 679){
			$(".login-clean").css("padding-top","120px");
		}
		if($(window).height() > 738){
			$(".login-clean").css("padding","140px 0");
		}
	}
	$(".toggleFormTgl").click(function(){
		$("#formTgl1.formTgl").toggleClass("hideForm");
		$("#formTgl2.formTgl").toggleClass("hideForm");
	});

	$("#btnSearch").click(function(){
		var key = $("#search-field").val();
		window.location.href = "content.php?qNama=" + key;
	})
});

function valLogin(){
	if(document.getElementsByName('username')[0].value === ""){
		alert('Input Username!');
		document.getElementsByName('username')[0].focus();
		return false;
	}
	else if(document.getElementsByName('password')[0].value === ""){
		alert('Input Password!');
		document.getElementsByName('password')[0].focus();
		return false;
	}
}

function gagalLogin(input){
	if(input == ""){
		alert('Username Salah!');
		document.getElementsByName('username')[0].focus();
	}else{
		alert('Password Salah!');
		document.getElementsByName('username')[0].value = input;
		document.getElementsByName('password')[0].focus();
	}
}

function cariTglSub(){
    var qKotas = document.getElementById("qKota").value;
    var qTgls = document.getElementById("qTgl").value;
    var qKarungs = document.getElementById("qKarung").value;
	if(qKotas === ""){
		alert("Lengkapi Kota, Tanggal, dan Karung! Atau Lihat List Tanggal Pengiriman dan pilih Tabel.");
	}
    else if(qTgls === ""){
		alert("Lengkapi Kota, Tanggal, dan Karung! Atau Lihat List Tanggal Pengiriman dan pilih Tabel.");
	}
    else if(qKarungs === ""){
		alert("Lengkapi Kota, Tanggal, dan Karung! Atau Lihat List Tanggal Pengiriman dan pilih Tabel.");
	}
	else{
		window.location.href = "content.php?q=" + qKotas + "_" + qTgls + "_" + qKarungs;
	}
}